import { useState } from "react";
import { Box, TextField, Button } from "@mui/material";
import axios from "axios";

const endpointMapping = {
  Notion: "notion",
  Airtable: "airtable",
  Hubspot: "hubspot",
};

export const DataForm = ({ integrationType, credentials }) => {
  const [loadedData, setLoadedData] = useState(null);
  const endpoint = endpointMapping[integrationType];

  const handleLoad = async () => {
    try {
      const endpointUrl =
        integrationType === 'Hubspot'
          ? `http://localhost:8000/integrations/${endpoint}/get_hubspot_items`
          : `http://localhost:8000/integrations/${endpoint}/load`;

      const response = await axios.post(endpointUrl, credentials, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setLoadedData(response.data);
    } catch (e) {
      alert(e?.response?.data?.detail || 'An error occurred');
    }
  };



  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      flexDirection="column"
      width="100%"
    >
      <Box display="flex" flexDirection="column" width="100%">
        <TextField
          label="Loaded Data"
          value={loadedData || ""}
          sx={{ mt: 2 }}
          InputLabelProps={{ shrink: true }}
          disabled
        />
        <Button onClick={handleLoad} sx={{ mt: 2 }} variant="contained">
          Load Data
        </Button>
        <Button
          onClick={() => setLoadedData(null)}
          sx={{ mt: 1 }}
          variant="contained"
        >
          Clear Data
        </Button>
      </Box>
    </Box>
  );
};
